import jspdf from "jspdf"
import autoTable from 'jspdf-autotable'

const createPDF = (data) => {

    const doc = new jspdf()
    const u = []
    let i = 1

    let addu = data.map(d =>{
        u.push(`['${i}', '${d.user}', '${d.fullname}', '${d.createdAt}', '${d.updatedAt}', '${d.enabled === 1 ? 'Activo' : 'Inactivo'}']`).toString
        i += 1
    })

    autoTable(doc, {
	head: [['No.', 'Usuario', 'Nombre y apellidos', 'F. Creado', 'F. Actualizado', 'Estado']],
	body: [u],
    })
    doc.save('usuarios.pdf')
//alert(u.toString())
}

export default createPDF
