import 'bootstrap/dist/css/bootstrap.css'
import 'bootstrap/dist/js/bootstrap.js'
import './App.css'
import { React, useState, useEffect, createContext } from 'react'
import background_app from './images/background_app.webp'
import { FaCircleCheck, FaTriangleExclamation, FaCircleExclamation } from "react-icons/fa6";
import { IoSunnyOutline, IoMoonOutline, IoLogOutOutline } from "react-icons/io5";
import { FaRegMoon } from "react-icons/fa";

// Import router
import { BrowserRouter, Route, Routes, Navigate } from 'react-router-dom'

// Import Components
import CompShowUsers from './pages/ShowUsers.js'
import CompCreateUser from './pages/CreateUser.js'
import CompEditUser from './pages/EditUser.js'
import CompLogin from './pages/CompLogin.js'
import CompPageNotFound from './pages/CompPageNotFound.js'

// Set backend server
export const serverContext = createContext()
const server = `http://${window.location.hostname}:4000/api`

const timestamp = Date.now()
const today = new Date(timestamp)
const currentYear = today.getFullYear()

// App Component
const App = () => {
  const [user, setUser] = useState('')
  const [notifyIcon, setNotifyIcon] = useState()
  const [notifyText, setNotifyText] = useState()
  const [theme, setTheme] = useState('')
  const [themeIcon, setThemeIcon] = useState(theme)

  useEffect(() => {
    getName()
    getTheme()
  }, [])

  const getName = (name) => {
    setUser(name)
  }

  const getTheme = () => {
    const dark = <FaRegMoon className='themeSwitch menuIcon' size='28px' />
    const light = <IoSunnyOutline className='themeSwitch menuIcon' size='28px' />

    const setDark = () => {
        setTheme("dark")
        setThemeIcon(dark)
    }

    const setLight = () => {
        setTheme("light")
        setThemeIcon(light)
    }

    if(window.matchMedia('(prefers-color-scheme: dark)').matches){
	setDark()
    } else {
	setLight()
    }

    window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change', e => {
    e.matches ? setDark() : setLight()
    }) 
  }

    const changeTheme = () => {
    const thm = document.getElementById('appDiv')

    if(theme === 'light'){
	thm.setAttribute('data-bs-theme','dark')
	window.location.reload(true)
    } else {
	thm.setAttribute('data-bs-theme','lught')
	window.location.reload(true)	
    }
  }

  const logOut = async () => {
    try {
      localStorage.removeItem('token')
      window.location.pathname = '/login'
    } catch (error) {
      console.log(error)
    }
  }

  // Notification Box

  const showNotification = (notiType, message) => {
    const noti = document.getElementById('s_notifications')

    switch (notiType) {
      case "ok":
        setNotifyIcon(
          <div id="ntf_icon" className="ntf_icon">
            <FaCircleCheck style={{ color: 'green' }} />
          </div>
        )
        break
      case "err":
        setNotifyIcon(
          <div id="ntf_icon" className="ntf_icon" >
            <FaTriangleExclamation style={{ color: 'red' }} />
          </div>
        )
        break
      case "inf":
        setNotifyIcon(
          <div id="ntf_icon" className="ntf_icon">
            <FaCircleExclamation style={{ color: 'yellow' }} />
          </div>
        )
        break
      default:
    }

    setNotifyText(message)
    noti.style['transform'] = 'translate(-3%)'
    setTimeout(() => {
      noti.style['transform'] = 'translate(102%)'
    }, 3500)

  }

  return (
    <serverContext.Provider value={server}>
      <>
        <div id='appDiv' data-bs-theme={theme} >
          <div className='App' >
            <nav className="navbar">
              <div className="container-fluid">
                <div className='Title'>
                  <p className="App-Title "><span className='text fw-bold mb-2 text-uppercase'>Usuarios del proxy</span></p>
                </div>
                <div className="sessionInfo d-inline-flex" id='logOut'>

                  <span className='userName' id='userName'>{user}</span>
                    < button className="btn" id='themeSwitch' type="button" onClick={changeTheme}>{themeIcon}</button>
                    < button className="btn" id='logOut' hidden={user !== undefined ? false : true} type="button" onClick={logOut} ><IoLogOutOutline className='logOut menuIcon' size='40px'  /></button>
                </div>
              </div>
            </nav >

            {/* Notification */}
            <section className="s_notifications" id="s_notifications">
              <div className="ntf_box" id="ntf_box">
                <div className="ntf_msg" id="ntf_msg">
                  <div className="ntf_icon">{notifyIcon}</div>
                  <div className="ntf_text">
                    {notifyText}
                  </div>
                </div>
              </div>
            </section>
            <br />
            <BrowserRouter forceRefresh={true}>
              <Routes>
                <Route path='/' element={<CompShowUsers
                  getname={getName} notify={showNotification} />} />
                <Route path='/login' element={<CompLogin />} notify={showNotification} />
                <Route path='/new' element={<CompCreateUser getname={getName} notify={showNotification} />} />
                <Route path='/edit/:id' element={<CompEditUser getname={getName} notify={showNotification} />} />
                <Route path='/error' element={<CompPageNotFound getname={getName} />} />
                <Route path='*' element={<Navigate to="/error" />} />

              </Routes>
            </BrowserRouter>

          </div>
        </div >
        <div className='footer1'>
          <p>Powered by KiniunDev™ - Copyright© {currentYear}</p>
        </div>
      </>
    </serverContext.Provider>
  )
}

export default App
