import jspdf from "jspdf"
import autoTable from 'jspdf-autotable'

const createPDF = (data) => {

    const doc = new jspdf()
    const u = []

    let i = 1

    let addu = data.map(d =>{
    u.push([`'${i}', '${d.user}', '${d.fullname}', '${d.createdAt}', '${d.updatedAt}', '${d.enabled === 1 ? 'Activo' : 'Inactivo'}'`])
        i += 1
    })

    let r = Object.keys(u).map((key) => [key, u[key]]);

    autoTable(doc, {
	head: [['No.', 'Usuario', 'Nombre y apellidos', 'F. Creado', 'F. Actualizado', 'Estado']],
	body: [['1',`${data[0].user}`, `${data[0].fullname}`, `${data[0].createdAt}`]],
    })
//    doc.save('usuarios.pdf')
alert((r))
}

export default createPDF
